  /* reduce empty space on the left *
  margin-right: 6px !important;
  /* reduce empty space on the right *
}

.tabbrowser-tab:not([selected="true"])[busy][pinned][busy] {
  margin-left: -1px !important;
  /* reduce empty space on the left *
  margin-right: 6px !important;
  /* reduce empty space on the right *
}

.tabbrowser-tab:not([selected="true"])[busy][pinned][busy]:hover {
  filter: drop-shadow(0 0 3px #f1f1f198) !important;
  /* keep the glow effect *
}

/* Source file https://github.com/MrOtherGuy/firefox-csshacks/tree/master/chrome/combined_favicon_and_tab_close_button.css made available under Mozilla Public License v. 2.0
See the above repository for updates as well as full license text. */

/* Show tab close button when cursor is over the tab icon */

/* inline_tab_audio_icons.css is recommended because otherwise you cannot mute the tab using the mute button *

.tab-content {
  pointer-events: none !important;
}

.tab-icon-image:not([busy]) {
  display: block !important;
}*/

:where(.tab-content:hover) .tab-icon-image,
:where(.tab-content:hover)>.tab-icon-stack {
  visibility: hidden !important;
}

.tab-close-button {
  order: -1 !important;
  display: flex !important;
  position: relative !important;
  margin-inline: -4px -20px !important;
  padding-inline-start: 7px !important;
  opacity: 0 !important;
  width: unset !important;
  pointer-events: auto !important;
}

.tab-close-button:hover {
  opacity: 1
}

.tabbrowser-tab[pinned] .tab-close-button {
  display: none !important;
}

/* Source file https://github.com/MrOtherGuy/firefox-csshacks/tree/master/chrome/hide_tabs_with_one_tab.css made available under Mozilla Public License v. 2.0
See the above repository for updates as well as full license text. */

/* Makes tabs toolbar items zero-height initially and sets enlarge them to fill up space equal to tab-min-height set on tabs. Optionally use privatemode_indicator_as_menu_button.css to replace main menu icon with private browsing indicator while tabs are hidden.

This will NOT work correctly if you have hidden tabs such as with tab groups extensions
*/

/* This style causes CSD window controls to disappear on linux, but you can kind of work around that by using window_control_fallback_for_custom_windows_theme.css *

:root[sizemode="normal"] #nav-bar {
  --uc-window-drag-space-width: 0px
}

#titlebar {
  -moz-appearance: none !important;
}

#TabsToolbar {
  min-height: 0px !important
}

#tabbrowser-tabs,
#tabbrowser-arrowscrollbox {
  min-height: 0 !important;
}

.accessibility-indicator,
.private-browsing-indicator {
  height: unset !important;
}

.accessibility-indicator>hbox {
  padding-block: 0 !important
}

.tabbrowser-tab {
  height: calc(var(--tab-min-height) + 2 * var(--tab-block-margin, 0px)) !important;
}

.tabbrowser-tab:only-of-type,
.tabbrowser-tab[first-visible-tab="true"][last-visible-tab="true"] {
  visibility: collapse !important;
  min-height: 0 !important;
  height: 0 !important;
}

/* Fix window controls not being clickable *
:root[tabsintitlebar] #toolbar-menubar[autohide="true"][inactive] {
  transition: height 0ms steps(1) 80ms !important;
}

:where(#nav-bar) {
  border-inline: var(--uc-window-drag-space-width, 0) solid var(--toolbar-bgcolor) !important;
}

@media (-moz-platform: linux) {
  #TabsToolbar .titlebar-buttonbox {
    align-items: stretch !important;
  }

  #TabsToolbar>.titlebar-buttonbox-container>.titlebar-buttonbox>.titlebar-button {
    transform: scale(0.8);
    margin-inline: -3px !important;
  }

  #TabsToolbar .titlebar-button>.toolbarbutton-icon {
    padding: 0 13px !important
  }
}

#tabbrowser-tabs:not([overflow])~#alltabs-button {
  display: none !important;
}

#tabbrowser-arrowscrollbox-periphery,
#private-browsing-indicator-with-label,
#TabsToolbar>.titlebar-buttonbox-container {
  contain: strict !important;
  contain-intrinsic-height: 0px !important;
}

#tabbrowser-arrowscrollbox-periphery {
  contain-intrinsic-width: 36px !important;
  padding-inline-end: 3px !important;
}

#private-browsing-indicator-with-label {
  contain-intrinsic-width: 116px !important;
}

#TabsToolbar>.titlebar-buttonbox-container {
  contain-intrinsic-width: var(--uc-window-control-width, 138px) !important;
  margin-bottom: 0 !important;
}

@media (-moz-platform: linux) {
  #TabsToolbar>.titlebar-buttonbox-container {
    contain-intrinsic-width: var(--uc-window-control-width, 105px)
  }
}

@media (-moz-platform: macos) {
  #TabsToolbar>.titlebar-buttonbox-container {
    contain-intrinsic-width: var(--uc-window-control-width, 72px);
  }
}

